      0 => '/img/0.1500.0.0.2.2.95/catalog/5731c00d7e6cf_chistotel.jpg',
      1 => '/img/0.1500.0.0.2.2.95/catalog/5731c00d7ee4f_chistotel_4.jpg',
      2 => '/img/0.1500.0.0.2.2.95/catalog/5731c00d7f1ab_chistotel_5.jpg',
      3 => '/img/0.1500.0.0.2.2.95/catalog/58441b185af5b_ZOI_7787.jpg',
